// src/App.js

import React, {Component} from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Debits from './components/Debits';
import Credits from './components/Credits';
import {v4 as uuidv4} from 'uuid';

const { debits, credits, currentUser, accountBalance } = this.state;
class App extends Component {

  componentDidMount(){  
    fetch("https://moj-api.herokuapp.com/debits")
      .then(res => res.json()
        .then(data => {
          this.setState({debits: data});
          this.setState({debit_total: this.debitHandler()});
          if(credits && debits) this.updatedBalance();
        }));

    fetch("https://moj-api.herokuapp.com/credits")
      .then(res => res.json()
        .then(data => {
          this.setState({credits: data});
          this.setState({credit_total: this.creditHandler()});
          if(credits && debits) this.updatedBalance();
        }));
  }

  addCredit = (desc, amount) => {
    credits.push({
      id: uuidv4(), 
      description: desc, 
      amount: amount, 
      date: new Date().toISOString()
    })
  }

  addDebit = (desc, amount) => {
    debits.push({
    id:uuidv4(), 
    description: desc, 
    amount: amount, 
    date: new Date().toISOString()
  })
}
  debitHandler = () => {
    if(debits){
      let debit_total = 0; 
      for (let i =0; i< debits.length; i++){
        debit_total += parseFloat(debits[i].amount) 
      }
      this.setState({debit_total: debit_total})
      return debit_total; 
    }
    return 0;
  }

  creditHandler = () => {
    if(credits){
      let credit_total=0;
      for (let i=0; i< credits.length; i++) {
        credit_total += parseFloat(credits[i].amount)
      }
      this.setState({credit_total: credit_total})
      return credit_total;
    }
    return 0;
  }

  updatedBalance = () => {
    this.setState({accountBalance:(this.creditHandler()-this.debitHandler()).toFixed(2)})
  }



  constructor() {  // Create and initialize state
    super(); 
    this.state = {
      accountBalance: 1234567.89,
      currentUser: {
        userName: 'Joe Smith',
        memberSince: '11/22/99',
      }
    }
  }

  // Update state's currentUser (userName) after "Log In" button is clicked
  mockLogIn = (logInInfo) => {  
    const newUser = {...currentUser}
    newUser.userName = logInInfo.userName
    this.setState({currentUser: newUser})
  }

  // Create Routes and React elements to be rendered using React components
  render() {  
    const HomeComponent = () => (<Home accountBalance={accountBalance}/>);
    const UserProfileComponent = () => (
      <UserProfile userName={currentUser.userName} memberSince={currentUser.memberSince}  />
    );
    const LogInComponent = () => (<LogIn user={currentUser} mockLogIn={this.mockLogIn} />)  // Pass props to "LogIn" component
    const CreditsComponent = () => (
    <Credits 

    />)  
    const DebitsComponent = () => (
    <Debits 
      balance = {accountBalance} 
      addDebit = {this.addDebit}
      refreshBalance = {this.refreshBalance} 
      debitHandler = {this.debitHandler} 
      debit_total = {debit_total} 
      credit_total = {credit_total} 
      debits = {debits}
    />)


    return (
      <Router>
        <div>
          <Route exact path="/" render={HomeComponent}/>
          <Route exact path="/userProfile" render={UserProfileComponent}/>
          <Route exact path="/login" render={LogInComponent}/>   
          <Route exact path="/credits" render={CreditsComponent}/>
          <Route exact path="/debits" render={DebitsComponent}/>

        </div>
      </Router>
    );
  }element
}

export default App;
