import { Link } from 'react-router-dom';
const Debits = ({
  addDebit, 
  updatedBalance, 
  debitTotalHandler, 
  balance, 
  debits}) => {
    const [amount, setAmount] = useState (0);

	const debitsView = () => {
    return debits.map((debit) => {
      let date = debit.date.slice(0, 10);
      return <li key={debit.id}>{debit.amount} {date}</li>
    }) 
  }

  const submitHandler = (e) =>{
    e.preventDefault(); 
    addDebit(amount); 
    debitTotalHandler();
    updatedBalance();
  }


  return (
    <div>
      <h1>Debits</h1>
      <h4>Balance: {balance}</h4>
      {debitsView()}
      <form onSubmit={addDebit}>
        <input placeholder='amount' type="number" name="amount" />
        <button type="submit" onSubmit={()=>submitHandler}>Add Debit</button>
      </form>
      <Link to="/">Home</Link>
    </div>
  )
}

export default Debits;
