// src/App.js

import React, {Component} from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Debits from './components/Debits';
import Credits from './components/Credits';
import {v4 as uuidv4} from 'uuid';

class App extends Component {



  componentDidMount(){  
    const {debits, credits} = this.state;
    fetch("https://moj-api.herokuapp.com/debits")
      .then(res => res.json()
        .then(data => {
          this.setState({debits: data});
          this.setState({totalDebit: this.calculateDebit()});
          if(credits && debits) this.refreshBalance();
        }));

    fetch("https://moj-api.herokuapp.com/credits")
      .then(res => res.json()
        .then(data => {
          this.setState({credits: data});
          this.setState({totalCredit: this.calculateCredit()});
          if(credits && debits) this.refreshBalance();
        }));
  }

  addCredit = (desc, amount) => {
    const {credits} = this.state;
      credits.push({
      id: uuidv4(), 
      description: desc, 
      amount: amount, 
      date: new Date().toISOString()
    })
  }

  addDebit = (desc, amount) => {
    const {debits} = this.state;
    debits.push({
    id:uuidv4(), 
    description: desc, 
    amount: amount, 
    date: new Date().toISOString()
  })
}
  calculateDebit = () => {
    const {debits} = this.state;
    //parse through this.state.debits
    //add together all the amounts and return it
    if(debits){
      let debitSum = 0; 
      for (let i =0; i< debits.length; i++){
        debitSum += parseFloat(debits[i].amount) 
      }
      this.setState({totalDebit: debitSum})
      return debitSum; 
    }
    return 0;
  }

  calculateCredit = () => {
    //parse through this.state.credits
    //add together all the amounts and return it
    const {credits} = this.state;
    if(credits){
      let creditSum=0;
      for (let i=0; i< credits.length; i++) {
        creditSum += parseFloat(credits[i].amount)
      }
      this.setState({totalCredit: creditSum})
      return creditSum;
    }
    return 0;
  }

  refreshBalance = () => {
    //balance = calculateCredit - calculateDebit
    this.setState({accountBalance:(this.calculateCredit()-this.calculateDebit()).toFixed(2)})
  }



  constructor() {  // Create and initialize state
    super(); 
    this.state = {
      accountBalance: 1234567.89,
      currentUser: {
        userName: 'Joe Smith',
        memberSince: '11/22/99',
      }
    }
  }

  // Update state's currentUser (userName) after "Log In" button is clicked
  mockLogIn = (logInInfo) => {  
    const {currentUser} = this.state;
    const newUser = {...currentUser}
    newUser.userName = logInInfo.userName
    this.setState({currentUser: newUser})
  }

  // Create Routes and React elements to be rendered using React components
  render() {  
    const {accountBalance, currentUser} = this.state;
    const HomeComponent = () => (<Home accountBalance={accountBalance}/>);
    const UserProfileComponent = () => (
      <UserProfile userName={currentUser.userName} memberSince={currentUser.memberSince}  />
    );
    const LogInComponent = () => (<LogIn user={currentUser} mockLogIn={this.mockLogIn} />)  // Pass props to "LogIn" component
    const CreditsComponent = () => (
    <Credits 

    />)  // Pass props to "LogIn" component
    const DebitsComponent = () => (<Debits  />)  // Pass props to "LogIn" component

    return (
      <Router>
        <div>
          <Route exact path="/" render={HomeComponent}/>
          <Route exact path="/userProfile" render={UserProfileComponent}/>
          <Route exact path="/login" render={LogInComponent}/>   
          <Route exact path="/credits" render={CreditsComponent}/>
          <Route exact path="/debits" render={DebitsComponent}/>

        </div>
      </Router>
    );
  }element
}

export default App;
